e:"icon icon-link"},void 0,!1,{fileName:"/Users/linchuan/IdeaProjects/ui/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-fc1b4974-ef29-46fc-aa0c-ea2ecdd14346.mdx"},this)},void 0,!1,{fileName:"/Users/linchuan/IdeaProjects/ui/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-fc1b4974-ef29-46fc-aa0c-ea2ecdd14346.mdx"},this),"Add a mode toggle"]},void 0,!0,{fileName:"/Users/linchuan/IdeaProjects/ui/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-fc1b4974-ef29-46fc-aa0c-ea2ecdd14346.mdx",lineNumber:106,columnNumber:1},this),(0,n.jsxDEV)(e.p,{children:"Place a mode toggle on your site to toggle between light and dark mode."},void 0,!1,{fileName:"/Users/linchuan/IdeaProjects/ui/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-fc1b4974-ef29-46fc-aa0c-ea2ecdd14346.mdx",lineNumber:108,columnNumber:1},this),(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:[(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-title":"","data-language":"tsx","data-theme":"default",children:"components/mode-toggle.tsx"},void 0,!1,{fileName:"/Users/linchuan/IdeaProjects/ui/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-fc1b4974-ef29-46fc-aa0c-ea2ecdd14346.mdx"},this),(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__withMeta__:!0,__rawString__:`import { Moon, Sun } from "lucide-react"

import { Button } from "@/components/ui/button"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"
import { useTheme } from "@/components/theme-provider"

export function ModeToggle() {
  const { setTheme } = useTheme()

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="outline" size="icon">
          <Sun className="h-[1.2rem] w-[1.2rem] rotate-0 scale-100 transition-all dark:-rotate-90 dark:scale-0" />
          <Moon className="absolute h-[1.2rem] w-[1.2rem] rotate-90 scale-0 transition-all dark:rotate-0 dark:scale-100" />
          <span className="sr-only">Toggle theme</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end">
        <DropdownMenuItem onClick={() => setTheme("light")}>
          Light
        </DropdownMenuItem>
        <DropdownMenuItem onClick={() => setTheme("dark")}>
          Dark
        </DropdownMenuItem>
        <DropdownMenuItem onClick={() => setTheme("system")}>
          System
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  )
}
